mapper.select:

/*	retrieveMaxBoardNo	*/
	SELECT MAX(BOARD.BOARD_NO)
	FROM   TB_BOARD BOARD

mapper.insert:

/*	createBoard	*/
	INSERT INTO TB_BOARD(
	  BOARD_NO
	, USER_CODE
	, BOARD_TITLE
	, BOARD_CONTENTS
	, BOARD_DATE
	) VALUES (
	  'B' || LPAD(BOARD_SEQ.NEXTVAL, 5, 0)
	, #{userCode}
	, #{boardTitle}
	, #{boardContents}
	, TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI')
	)

mapper.update:

/*	updateBoard	*/
	UPDATE TB_BOARD
	SET BOARD_TITLE          =  #{boardTitle}
	  , BOARD_CONTENTS       =  #{boardContents}
	WHERE BOARD_NO = #{boardNo}


/*	updateBoardHits	*/
	UPDATE TB_BOARD
	SET BOARD_HITS          =  BOARD_HITS + 1
	WHERE BOARD_NO = #{boardNo}

mapper.delete:

/*	deleteBoard	*/
	DELETE
	FROM  TB_BOARD
	WHERE 1=1
	AND BOARD_NO = #{boardNo}
